mapper.update:
update board_detail
set title=#{title}, contents=#{contents}, postdate=sysdate
where boardno = #{boardNo}

mapper.select:
select boardno, title, contents, to_char(postdate,'yyyy-MM-dd'), boardtypecode, userid
from board_detail
where boardNo = #{boardNo}

		select count(boardTypeCode)
 		  from board_detail
 		 where boardTypeCode = #{boardTypeCode}

select boardNo, title, contents, to_char(postDate,'yyyy-MM-dd') postDate, boardTypeCode, userId, views
  from board_detail
 where boardNo = #{boardNo}

mapper.insert:
        update board_detail
		   set title=#{title}, contents=#{contents}, postdate=sysdate
         where boardNo = #{boardNo}

mapper.select:
select count(boardTypeCode)
  from board_detail
 where boardTypeCode = #{boardTypeCode}

        select boardNo, title, contents, to_char(postDate,'yyyy-MM-dd') postDate, boardTypeCode, userId 
        from board_detail 
		where postDate between #{startDate}
		and #{endDate}
        and title like '%' || #{title} || '%'

        select boardNo, title, contents, to_char(postDate,'yyyy-MM-dd') postDate, boardTypeCode, userId 
        from board_detail 
		where postDate between #{startDate}
		and #{endDate}
        and contents like '%' || #{contents} || '%'

        select boardNo, title, contents, to_char(postDate,'yyyy-MM-dd') postDate, boardTypeCode, userId
        from board_detail 
		where postDate between #{startDate}
		and #{endDate}
        and userId like '%' || #{userId} || '%'

mapper.sql:
    	where postDate between #{startDate}
		and #{endDate}
    	and #{searchType} like '%' || #{keyword} || '%'

mapper.select:
select boardNo, title, contents, postDate, boardTypeCode, userId, views, (SELECT COUNT(*) FROM ATTACH_BOARD WHERE boardNo=a.boardNo) filecnt from (select a.boardNo boardNo, a.title title, a.contents contents, to_char(a.postDate,'yyyy-MM-dd') postDate, a.boardTypeCode boardTypeCode, a.userId userId, a.views views, rownum rn from (select boardNo, title, contents, postDate, boardTypeCode, userId, views from board_detail where boardTypeCode = #{boardTypeCode} order by boardNo desc) a ) a where rn between (#{page}-1) * #{pageSize}+1 and #{page}*#{pageSize}
and title like '%' || #{title} || '%'
and contents like '%' || #{contents} || '%'
and userid like '%' || #{userId} || '%'
and postDate between #{startDate} and #{endDate}

mapper.update:
		update board_detail
  		   set views = views + 1 
		 where boardno = #{boardNo}
