mapper.select:
select boardno, title, contents, postdate, boardtypecode, userid
from board_detail
where boardno = #{boardNo}

		select count(*)
 		from board_detail
 		where boardTypeCode = #{boardTypeCode}

mapper.update:
update board_detail
set title=#{title}, contents=#{contents}, postdate=sysdate, boardtypecode=#{boardTypeCode}, userid=#{userId}
where boardno = #{boardNo}

mapper.select:
select * from ( select a.*, rownum rn from (
select board_detail.* from board_detail where boardTypeCode = #{boardTypeCode} order by boardNo ) a ) a
where rn between (#{page}-1) * #{pageSize}+1 and #{page}*#{pageSize}
