mapper.insert:
	
	  	select 
	  		IMAGES_SEQ.NEXTVAL
	  	from 
	  		dual
    
	insert into images
	(imagecode, imagename, imagerealpath, imagerealname, usename, userid)
	values
	(#{imageCode}
	,#{imageName}
	,#{imageRealpath}
	,#{imageRealname}
	,nvl(#{userName, jdbcType=VARCHAR},null)
	,nvl(#{userId, jdbcType=VARCHAR},null))
